INSERT INTO `tbl_movie`(`id`, `name`, `producer`, `showTime`, `ticketPrice`, `introduce`, `rank`, `movieType`, `createTime`) VALUES ('2a5e849c6b384d33bc6eb61da982f358', '真·三国无双', 'warner', '2021-05-06', '25', '动荡的东汉末年，汉室摇摇欲坠，董卓入京霸占朝野，引起天下动荡，身怀绝世武艺的曹操、吕布、关羽、刘备、张飞等各路英雄豪杰纷纷崛起，一场群雄逐鹿的大混战正式拉开序幕。在乱世中，到底谁才是真正的无双英雄 。', '4.5', '动作，奇幻，战争', '2021-05-19 19:00:00');
